unread_subreddit.map:
function(doc) {
    if (doc['stevia_type'] == 'thread' && !(doc.stevia_downloaded && doc.stevia_downloaded == 'true')  ) {
       emit(doc.subreddit, {
         _id: doc._id,
         title: doc.title,
         fullname: doc.fullname
       });
    }
}

unread_subreddit_time.map:
function(doc) {
    if (doc['stevia_type'] == 'thread' && !(doc.stevia_downloaded && doc.stevia_downloaded == 'true')  ) {
       emit([doc.subreddit, doc.created_utc], {
         _id: doc._id,
         title: doc.title,
         fullname: doc.fullname
       });
    }
}
